function Header() {
  return (
    <header className="bg-gradient-to-r from-blue-700 to-blue-500 text-white p-4 shadow-md">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold tracking-wide">ðŸšš TruckBook</h1>
        <nav className="space-x-4 text-lg">
          <Link to="/" className="hover:text-yellow-300 inline-flex items-center gap-1"><HomeIcon size={18}/> Home</Link>
          <Link to="/trucks" className="hover:text-yellow-300 inline-flex items-center gap-1"><Truck size={18}/> Book Truck</Link>
        </nav>
      </div>
    </header>
  );
}

export default Header;
