import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

function Booking() {
  const { id } = useParams();
  const [form, setForm] = useState({
    name: '',
    pickup: '',
    drop: '',
    date: '',
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Truck ID ${id} booked for ${form.name} from ${form.pickup} to ${form.drop}`);
  };

  return (
    <div className="max-w-md mx-auto mt-6">
      <h2 className="text-xl font-bold mb-4">Book Truck #{id}</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input name="name" placeholder="Your Name" className="w-full p-2 border rounded" onChange={handleChange} required />
        <input name="pickup" placeholder="Pickup Location" className="w-full p-2 border rounded" onChange={handleChange} required />
        <input name="drop" placeholder="Drop Location" className="w-full p-2 border rounded" onChange={handleChange} required />
        <input name="date" type="date" className="w-full p-2 border rounded" onChange={handleChange} required />
        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded">Confirm Booking</button>
      </form>
    </div>
  );
}

export default Booking;
