import React from 'react';
import { Link } from 'react-router-dom';
import { TruckIcon } from 'lucide-react';

const trucks = [
  { id: 1, name: 'Tata 407', capacity: '2 Tons', price: '₹1500/day' },
  { id: 2, name: 'Eicher 19Ft', capacity: '7 Tons', price: '₹3500/day' },
  { id: 3, name: 'Mahindra Loadking', capacity: '3.5 Tons', price: '₹2500/day' },
];

function Trucks() {
  return (
    <div>
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">Available Trucks</h2>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {trucks.map((truck) => (
          <div
            key={truck.id}
            className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg border border-gray-200"
          >
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-xl font-semibold">{truck.name}</h3>
              <TruckIcon className="text-blue-500" />
            </div>
            <p className="text-gray-600 mb-2">Capacity: {truck.capacity}</p>
            <p className="text-gray-700 font-medium mb-4">Price: {truck.price}</p>
            <Link
              to={`/booking/${truck.id}`}
              className="block text-center bg-green-600 hover:bg-green-700 text-white py-2 rounded-full font-medium"
            >
              Book Now
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Trucks;
